<template>
  <a-card
    :loading="loading"
    hoverable
    class="picture-item"
    style="width: 220px">
    <template #cover>
      <a-image
        :src="url"
        alt="preview"
        fallback="data:image/png;base64,"
      />
    </template>

    <template #actions>
      <a-tooltip title="编辑图片">
        <a-button
          type="link"
          @click="handleEditBtnClick"
        >
          <DynamicIcon icon="EditOutlined" />
        </a-button>
      </a-tooltip>

      <a-tooltip :title="picture.isShow ? '隐藏图片' : '显示图片'">
        <a-button
          type="link"
          v-bind="{
            [picture.isShow ? 'default' : 'danger']: true
          }"
          @click="handleIsShowBtnClick"
        >
          <DynamicIcon
            :icon="picture.isShow ? 'EyeOutlined' : 'EyeInvisibleOutlined'"
          />
        </a-button>
      </a-tooltip>

      <a-popconfirm
        title="确定要删除该图片吗？"
        @confirm="handleDeleteButtonClick"
      >
        <a-button
          type="link"
          danger>
          <DynamicIcon icon="DeleteOutlined" />
        </a-button>
      </a-popconfirm>
    </template>

    <a-card-meta>
      <template #title>
        <a-tooltip :title="picture.name">
          {{ picture.name }}
        </a-tooltip>
      </template>
    </a-card-meta>
  </a-card>
</template>

<script setup lang="ts">
import { type PictureItem } from '@/types/picture'
import DynamicIcon from '@/components/common/DynamicIcon'
import { deletePicture, togglePictureIsShow } from '@/services'
import { type BaseResponse } from '@/types/common'
import { success } from '@/utils/http'
import { useRequest } from '@/composables'

const props = defineProps<{
  picture: PictureItem
}>()

const emit = defineEmits<(e: 'refresh') => void>()

const router = useRouter()

const isShowLoading = ref(false)

const { loading: deleteLoading, fetch: deleteFetch } = useRequest<BaseResponse<null>, [number]>(deletePicture)
// const { loading: isShowLoading, handleToggleIsShow } = useToggleIsShow(togglePictureIsShow, () => {
//   success('操作成功')
//   emit('refresh')
// })

const url = computed(() => {
  return (props.picture.qiniuDomain && props.picture.qiniuKey)
    ? props.picture.qiniuDomain + props.picture.qiniuKey
    : ''
})

const loading = computed(() => deleteLoading.value || isShowLoading.value)

const handleEditBtnClick = () => {
  router.push({
    name: '编辑图片',
    params: {
      id: props.picture.id
    }
  })
}

const handleIsShowBtnClick = async () => {
  isShowLoading.value = true

  try {
    const res = await togglePictureIsShow(props.picture.id, props.picture.isShow === 1 ? 0 : 1)

    if (res?.code !== 200) {
      // eslint-disable-next-line @typescript-eslint/no-throw-literal
      throw res
    }

    success('操作成功')
    emit('refresh')
  } finally {
    isShowLoading.value = false
  }
}

const handleDeleteButtonClick = async () => {
  await deleteFetch(props.picture.id)
  success('操作成功')
  emit('refresh')
}
</script>

<style lang="scss" scoped>
.picture-item {
  // border: 1px solid #303030 !important;
  :deep(.ant-card-cover .ant-image) {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 220px;
    height: 180px !important;

    > img {
      width: auto;
      max-width: 220px;
      max-height: 180px;
    }
  }
}
</style>
