<template>
  <div class="page-sys-user-list">
    <a-spin :spinning="spinning">
      <query-list
        row-key="id"
        :columns="columns"
        :data-source="dataSource"
        :pagination="pagination"
        :selected-row-keys="selectedRowKeys"
        :loading="loading"
        @table-change="handleTableChange">
        <template #customOperations>
          <a-button
            type="primary"
            @click="handleAdd">
            <plus-outlined />
            添加新用户
          </a-button>
        </template>

        <template #bodyCell="{ column, record }">
          <template v-if="column.dataIndex === 'avatarPic'">
            <a-image
              :width="60"
              :height="60"
              :src="record.avatarPic"
              fallback="data:image/png;base64,"
            />
          </template>

          <template v-if="column.dataIndex === 'isLock'">
            <BooleanCell :value="record.isLock" />
          </template>

          <template v-if="column.dataIndex === 'isShow'">
            <BooleanCell :value="record.isShow" />
          </template>

          <template v-if="column.dataIndex === 'operations'">
            <ListOperations :operations="getOperations(record)"></ListOperations>
          </template>
        </template>
      </query-list>
    </a-spin>

    <SetRoleModal
      v-if="setRoleModalVisible"
      :user-info="userInfo"
      @success="handleSetRoleSuccess"
      @close="setRoleModalVisible = false"
    />
  </div>
</template>

<script setup lang="ts">
import { QueryList, BooleanCell, ListOperations } from '@/components/common/queryList';
import { useDefineAsyncComponent } from '@/hooks/common';
import { useDeleteUser, useUserManage, useUserList } from '@/hooks/sys/user';
import { IUserInfo } from '@/types/user';
import { computed, onActivated, ref } from 'vue';
import { useRouter } from 'vue-router';

const SetRoleModal = useDefineAsyncComponent('/components/sys/user/SetRoleModal.vue');

const router = useRouter();
const setRoleModalVisible = ref(false);
const userInfo = ref<IUserInfo | null>(null);

const { loading: lockLoading, handleManage } = useUserManage(async () => {
  await handleSearch()
});
const { loading: deleteLoading, handleDelete } = useDeleteUser(async () => {
  await handleSearch()
});

const handleSetRoleBtnClick = (info: IUserInfo) => {
  userInfo.value = info;
  setRoleModalVisible.value = true;
}

const {
  columns,
  dataSource,
  pagination,
  selectedRowKeys,
  loading,
  handleSearch,
  handleTableChange,
  getOperations
} = useUserList({ handleManage, handleDelete, handleSetRoleBtnClick });

const spinning = computed(() => {
  return lockLoading.value || deleteLoading.value;
})

onActivated(async () => {
  await handleSearch();
});

const handleAdd = () => {
  router.push({ name: '新增用户' });
}

const handleSetRoleSuccess = () => {
  setRoleModalVisible.value = false;
  handleSearch();
}
</script>
